	/**
	 * Method perform an INSERT on the database, given a AlpzaComite or Criteria object.
	 *
	 * @param      mixed $values Criteria or AlpzaComite object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
		// symfony_behaviors behavior
		foreach (sfMixer::getCallables('BaseAlpzaComitePeer:doInsert:pre') as $sf_hook)
		{
			if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseAlpzaComitePeer', $values, $con))
			{
				return $sf_hook_retval;
			}
		}

		if ($con === null) {
			$con = Propel::getConnection(AlpzaComitePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from AlpzaComite object
		}

		if ($criteria->containsKey(AlpzaComitePeer::ID_ALPZA_COMITE) && $criteria->keyContainsValue(AlpzaComitePeer::ID_ALPZA_COMITE) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.AlpzaComitePeer::ID_ALPZA_COMITE.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables('BaseAlpzaComitePeer:doInsert:post') as $sf_hook)
		{
			call_user_func($sf_hook, 'BaseAlpzaComitePeer', $values, $con, $pk);
		}

		return $pk;
	}

