			$selectCriteria->add(AlpzaComitePeer::ID_ALPZA_COMITE, $criteria->remove(AlpzaComitePeer::ID_ALPZA_COMITE), $comparison);

		} else { // $values is AlpzaComite object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

