	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = AlpzaMiembroGeneralPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(AlpzaMiembroGeneralPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(AlpzaMiembroGeneralPeer::DATABASE_NAME);
		$criteria->add(AlpzaMiembroGeneralPeer::ID_ALPZA_MIEMBRO_GENERAL, $pk);

		$v = AlpzaMiembroGeneralPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
